import { Component } from '@angular/core';

@Component({
  selector: 'app-to-do-page',
  templateUrl: './to-do-page.component.html',
  styleUrls: ['./to-do-page.component.css']
})
export class ToDoPageComponent {
  newToDo = "";
  todos : string[] = [];

  newElement = "";
  index : number | null = null;
  addToDo() {
    if(this.newToDo){
      this.todos.push(this.newToDo);
      this.newToDo = "";
    }
    console.log(this.todos);
  }

  removeToDo(){
    this.todos.pop();
  }
  editToDo(){
    if(this.index != null && this.index < this.todos.length){
      this.todos[this.index] = this.newElement;
    }else{
      console.log("Index out of range.");
      console.log(this.index);
      console.log(this.newElement);
    }
  }

}
<div><p>to-do-page works!</p></div>


<form (ngSubmit)="addToDo()">
    <input type="text" [(ngModel)]="newToDo" name="newToDo">
    <button type="submit">Add Todo</button>
    <button (click)="removeToDo()">delete</button>

</form>
<form (ngSubmit)="editToDo()">
    <input type="text" [(ngModel)]="newElement" name="newElement" placeholder="text">
    <input type="number" [(ngModel)]="index" name="index" placeholder="index">
    <button type="submit">Update</button>

</form>

<ul>
    <li *ngFor="let todo of todos">{{todo}}</li>    
</ul>
